class NewsItem {
  final String title;
  final String description;
  final String imageUrl;
  final String source;
  final String publishedAt;

  NewsItem({
    required this.title,
    required this.description,
    required this.imageUrl,
    required this.source,
    required this.publishedAt,
  });
}

